"use client";

import { useState, useEffect } from "react";
import { signIn } from "next-auth/react";
// import Image from "next/image";

import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import Checkbox from '@mui/material/Checkbox';
import { publicEnv } from "@/lib/env/public";
import Resizer from "react-image-file-resizer";
import useUsers from "@/hooks/useUsers";
import AuthInput from "./AuthInput";

type UniqueUser = {
  displayId: true,
  email: string,
  username: string,
}
function AuthForm() {
  const [email, setEmail] = useState<string>("");
  const [username, setUsername] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const [confirmPassword, setConfirmPassword] = useState<string>("");
  const [isSignUp, setIsSignUp] = useState<boolean>(false);
  const [isCoach, setIsCoach] = useState<boolean>(false);

  const [avatarUrl, setAvatarUrl] = useState<string>("data:image/png;base64,")
  const { getAllUsers } = useUsers();
  const [users, setUsers] = useState<UniqueUser[]>([]);
  const fetchUsers = async () => {
    try {
      const targetUsers = await getAllUsers();
      console.log(targetUsers);
      setUsers(targetUsers);
    } catch (error) {
      console.log(error);
      alert("Error getting coachinfo");
    }
  };
  useEffect(() => {
    fetchUsers();
  }, []);

  const handleImg = async (e:React.ChangeEvent<HTMLInputElement>) => {
    try {
      if(e.target.files === null) return;
      const file = e.target.files[0];
      const image = await resizeFile(file);
      setAvatarUrl(typeof image === 'string' ? image : "");
      // console.log(image);
    } catch (err) {
      console.log(err);
    }
  }

  const resizeFile = (file: Blob) =>
    new Promise((resolve) => {
      Resizer.imageFileResizer(
        file,
        150,
        150,
        "JPEG",
        150,
        0,
        (uri) => {
          resolve(uri);
        },
        "base128",
      );
    });

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    // TODO: sign in logic
    if (isSignUp) {
      if (!avatarUrl) {
        alert("Avatar is required");
        return;
      }
      if((users.find(e => e.email===email))!==undefined)
      {
        alert("email 已被註冊");
        return;
      }
      if((users.find(e => e.username===username))!==undefined)
      {
        alert("username 已被註冊");
        return;
      }
      if (password.length < 4) {
        alert("Password has to be longer than 4 characters!");
        return;
      }
      if (password !== confirmPassword) {
        alert("Passwords are inconsistent.");
        return;
      }
    }

    signIn("credentials", {
      isCoach,
      email,
      username,
      password,
      avatarUrl,
      callbackUrl: `${publicEnv.NEXT_PUBLIC_BASE_URL}/main`,
    });
  };
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setIsCoach(event.target.checked);
  };
  return (
    <Card className="minfalse-w-[300px]">
      <CardHeader>
        <CardTitle>Sign {isSignUp ? "Up" : "In"}</CardTitle>
      </CardHeader>
      <CardContent className=" flex flex-col gap-2">
        <form onSubmit={handleSubmit} className="flex flex-col gap-2">
          {isSignUp && (
            <>
            <div className="flex items-center space-x-2">
              <Checkbox
                checked={isCoach}
                onChange={handleChange}
                inputProps={{ 'aria-label': 'controlled' }}
              />
              <span>以教練身份登入</span>
            </div>
            <div className="flex">
              <picture>
                <img
                  src={avatarUrl}
                  // width={48}
                  // height={30}
                  className="w-12 h-12 rounded-full mr-1 mt-0.5"
                  alt={"image"}
                />
              </picture>
              <div>
                <div>Avatar</div>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleImg}
                  className="block w-fit text-sm text-slate-500 border-0 bg-transparent drop-shadow-none
                    file:mr-4 file:py-1 file:px-2
                    file:rounded-full file:border-0
                    file:text-sx
                    file:bg-slate-50 file:text-slate-700
                    hover:file:bg-slate-100
                  "
                />
              </div>
            </div>
            </>
          )}
          <AuthInput
            label="Email"
            type="email"
            value={email}
            setValue={setEmail}
          />
          {isSignUp && (
            <AuthInput
              label="Username"
              type="text"
              value={username}
              setValue={setUsername}
            />
          )}
          <AuthInput
            label="Password"
            type="password"
            value={password}
            setValue={setPassword}
          />
          {isSignUp && (
            <AuthInput
              label="Confirm Password"
              type="password"
              value={confirmPassword}
              setValue={setConfirmPassword}
            />
          )}
          <div className="text-sm text-gray-500">
            {isSignUp ? (
              <span>
                Already have an account?{" "}
                <a
                  className="cursor-pointer hover:underline"
                  onClick={() => setIsSignUp(false)}
                >
                  Sign In
                </a>
              </span>
            ) : (
              <span>
                Do not have an account?{" "}
                <a
                  className="cursor-pointer hover:underline"
                  onClick={() => setIsSignUp(true)}
                >
                  Sign Up
                </a>
              </span>
            )}
          </div>

          <Button type="submit" className="w-full">
            Sign {isSignUp ? "Up" : "In"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

export default AuthForm;
